import { getBestScore } from '../helpers/getBestScore'
import Timer from './timerComponent'
import { Iobj, Iarray, itemType } from '../types/types'
import _ from 'lodash'

function memoryGame(props: {
  minute: string
  seconds: string
  imageArray: Iarray
  clickedItem2: Iobj
  clickedItem1: Iobj
  handleClick: (item: itemType, index: number) => void
}) {
  return (
    <div className="max-w-max mx-auto pt-2">
      <div className="flex justify-between items-center pb-5">
        {getBestScore(localStorage.getItem('data')) ? (
          <p>Your highscore : {getBestScore(localStorage.getItem('data'))}</p>
        ) : (
          <></>
        )}

        {props.minute && props.seconds ? (
          <Timer minute={props.minute} seconds={props.seconds} />
        ) : (
          <></>
        )}
      </div>
      <ul className="pl-0 m-auto max-w-max grid grid-cols-4 gap-3">
        {_.map(props.imageArray, (item: itemType, index: number) => {
          return (
            <li
              key={index}
              className={`list-none relative pointer rounded-md border border-transparent w-32 h-32 ${
                item.class
              } ${
                index === props.clickedItem1.ind ||
                index === props.clickedItem2.ind
                  ? 'flip'
                  : ''
              }`}
              onClick={() => props.handleClick(item, index)}
            >
              <img
                src={item.img}
                alt=""
                className="w-32 h-32 object-cover absolute left-0 top-0 rounded-md"
              ></img>
              <div className="w-32 h-32 object-cover absolute left-0 top-0 rounded-md layer"></div>
            </li>
          )
        })}
      </ul>
    </div>
  )
}
export default memoryGame
